  //   );
  // }
  useEffect(() => {
    axios.get("http://localhost:8000/records")
      .then(response => {setRecords(response.data);
    })
    .catch(error => {
      console.log(error);
    });
  }, []);

  return (
    <div>
      <Header />
      <CreateArea onAdd={addRecord} />
      {records.map((recordItem, index) => {
        return (
          <Record
            key={index}
            id={index}
            title={recordItem.title}
            content={recordItem.content}
            onDelete={deleteRecord}
          />
        );
      })}
      <Footer />
    </div>
  );
}
  export default App;
